    n, err := conn.Read(buf)
    if err != nil {
        return
    }
    fileName = string(buf[:n])
    addr := conn.RemoteAddr()
    fmt.Printf("[%s]：%s", addr, fileName)
    sendOkToClient(conn)
    reciveFromClient(conn)
}
